set @league = 'NCAA';
select concat(concat(concat(concat('import ',concat(REPLACE(concat(t.location, t.name), ' ', ''),'Logo')), concat(concat(' from "./teamLogos/'), lcase(@league)), '/'), REPLACE(concat(t.location, t.name), ' ', ''),'Logo'), '.svg";') from team t where league = @league;
select concat(concat(concat(concat('case ',t.id), ': '), 'return '), concat(REPLACE(concat(t.location, t.name), ' ', ''),'Logo;')) from team t WHERE league = @league;